<template>\n  <div>\n    <h2>My Clients</h2>\n    <ul>\n      <li v-for=\"client in clients\" :key=\"client.id\">\n        {{ client.name }}\n        <button @click=\"assignWorkout(client)\">Assign Workout</button>\n      </li>\n    </ul>\n  </div>\n</template>\n\n<script setup>\nimport { ref } from \'vue\'\n\nconst clients = ref([\n  { id: 1, name: \'Client A\' },\n  { id: 2, name: \'Client B\' },\n  { id: 3, name: \'Client C\' }\n])\n\nconst emit = defineEmits([\'assign-workout\'])\n\nconst assignWorkout = (client) => {\n  emit(\'assign-workout\', client)\n}\n</script>